import React, { useState } from "react";
import { Children } from "react";
import './caraousel.css'




function Carousel(props){
    const [activeItem,setactiveItem]=useState(0);

    const updateActiveItem =(count)=>{
        console.log(activeItem,count);
        let newItemIndex=activeItem;
        newItemIndex=newItemIndex+count;
        const totalChildren=Children.count(props.children);
        console.log("total children",totalChildren);
        if(newItemIndex<0){
            newItemIndex=0;
        }
        else if(newItemIndex>= totalChildren){
            newItemIndex= totalChildren-1;
        }
        setactiveItem(newItemIndex);
    }

    return(
      <div className="carousel">
          <button onClick={()=>{
              updateActiveItem(1)
          }}>prev</button>
          <div>
       {Children.map(props.children,(child,index)=>{
           if(index===activeItem){
           return React.cloneElement(child)
           }
       })}
       </div>
       <button onClick={()=>{
           updateActiveItem(-1)
       }}
       >next</button>
      </div>
    )
  }
  export default Carousel;
